import React from "react";
import { useNavigate, useParams } from "react-router-dom";

function PdfViewer() {
  const navigate = useNavigate();
  const { fileName } = useParams(); // get the PDF file name from URL

  return (
    <div
      style={{
        textAlign: "center",
        background: "#0f172a",
        minHeight: "100vh",
        color: "white",
      }}
    >
      {/* ✅ Back Button */}
      <button
        onClick={() => navigate(-1)}
        style={{
          margin: "20px",
          padding: "10px 20px",
          borderRadius: "8px",
          background: "#fbbf24",
          color: "black",
          fontWeight: "bold",
          cursor: "pointer",
        }}
      >
        ⬅ Back
      </button>

      {/* ✅ PDF inside iframe */}
      <iframe
        src={`/pdfs/${fileName}`}
        title="PDF Viewer"
        style={{ width: "90%", height: "90vh", border: "none" }}
      />
    </div>
  );
}

export default PdfViewer;
