import React, { useState, useEffect } from "react";

function MockInterview() {
  const [timeLeft, setTimeLeft] = useState(120); // 2 min timer
  const [answer, setAnswer] = useState("");

  // Sample questions
  const questions = [
    "Tell me about yourself.",
    "Why should we hire you?",
    "Explain a challenging project you worked on.",
    "Where do you see yourself in 5 years?",
  ];

  const [currentQ, setCurrentQ] = useState(0);

  // Timer logic
  useEffect(() => {
    if (timeLeft === 0) return;
    const timer = setInterval(() => setTimeLeft(timeLeft - 1), 1000);
    return () => clearInterval(timer);
  }, [timeLeft]);

  return (
    <div
      style={{
        backgroundColor: "#0f172a",
        minHeight: "100vh",
        color: "white",
        padding: "30px",
      }}
    >
      <h1
        style={{
          textAlign: "center",
          fontSize: "34px",
          fontWeight: "bold",
          marginBottom: "20px",
          color: "#f97316",
        }}
      >
        ðŸŽ¤ Mock Interview Practice
      </h1>

      {/* Question */}
      <div
        style={{
          background: "#1e293b",
          padding: "25px",
          borderRadius: "12px",
          boxShadow: "0px 6px 16px rgba(0,0,0,0.5)",
          maxWidth: "800px",
          margin: "auto",
        }}
      >
        <h2 style={{ color: "#facc15", marginBottom: "10px" }}>
          Q{currentQ + 1}: {questions[currentQ]}
        </h2>

        {/* Timer */}
        <p style={{ color: "#22d3ee", fontWeight: "bold" }}>
          Time Left: {Math.floor(timeLeft / 60)}:{("0" + (timeLeft % 60)).slice(-2)}
        </p>

        {/* Answer box */}
        <textarea
          rows="6"
          value={answer}
          onChange={(e) => setAnswer(e.target.value)}
          placeholder="Type your answer here..."
          style={{
            width: "100%",
            marginTop: "15px",
            padding: "12px",
            borderRadius: "10px",
            border: "none",
            background: "#0f172a",
            color: "white",
            fontSize: "16px",
          }}
        />

        {/* Buttons */}
        <div style={{ marginTop: "20px", display: "flex", gap: "10px" }}>
          <button
            onClick={() => {
              setAnswer("");
              setTimeLeft(120);
              setCurrentQ((prev) => (prev + 1) % questions.length);
            }}
            style={{
              background: "#22d3ee",
              padding: "10px 20px",
              borderRadius: "8px",
              border: "none",
              color: "#0f172a",
              fontWeight: "bold",
              cursor: "pointer",
            }}
          >
            Next Question
          </button>
          <button
            onClick={() => {
              alert("Your Answer:\n\n" + answer);
            }}
            style={{
              background: "#fbbf24",
              padding: "10px 20px",
              borderRadius: "8px",
              border: "none",
              color: "#0f172a",
              fontWeight: "bold",
              cursor: "pointer",
            }}
          >
            Submit Answer
          </button>
        </div>
      </div>
    </div>
  );
}

export default MockInterview;
