import React from "react";
import { useParams, useNavigate } from "react-router-dom";

function PdfViewer() {
  const { fileName } = useParams();
  const navigate = useNavigate();

  return (
    <div
      style={{
        backgroundColor: "#0f172a",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        color: "white",
      }}
    >
      {/* Header with Back Button */}
      <div
        style={{
          display: "flex",
          alignItems: "center",
          padding: "15px",
          backgroundColor: "#1e293b",
          boxShadow: "0 2px 8px rgba(0,0,0,0.4)",
        }}
      >
        <button
          onClick={() => navigate(-1)} // ðŸ‘ˆ Go back to previous page
          style={{
            padding: "8px 16px",
            backgroundColor: "#facc15",
            border: "none",
            borderRadius: "6px",
            fontWeight: "bold",
            cursor: "pointer",
            marginRight: "15px",
          }}
        >
          â¬… Back
        </button>
        <h2 style={{ fontSize: "20px", fontWeight: "bold", color: "#facc15" }}>
          {fileName}
        </h2>
      </div>

      {/* PDF Display */}
      <iframe
        src={`/pdf/${fileName}`}
        title={fileName}
        style={{
          flex: 1,
          width: "100%",
          border: "none",
        }}
      />
    </div>
  );
}

export default PdfViewer;
