import React, { useState, useRef } from "react";

const questions = {
  hr: [
    "Tell me about yourself.",
    "Why should we hire you?",
    "What are your strengths and weaknesses?",
  ],
  technical: [
    "Explain the difference between stack and queue.",
    "What are the four pillars of OOPs?",
    "How does a database index improve performance?",
  ],
};

function MockInterview() {
  const [mode, setMode] = useState(null);
  const [currentQ, setCurrentQ] = useState(0);
  const [completed, setCompleted] = useState(false);
  const [recordings, setRecordings] = useState({});

  const mediaRecorderRef = useRef(null);
  const chunksRef = useRef([]);
  const videoRef = useRef(null);
  const [recording, setRecording] = useState(false);
  const [stream, setStream] = useState(null);

  // Start Video Recording
  const startRecording = async () => {
    const mediaStream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
    setStream(mediaStream);

    if (videoRef.current) {
      videoRef.current.srcObject = mediaStream;
    }

    mediaRecorderRef.current = new MediaRecorder(mediaStream);
    mediaRecorderRef.current.ondataavailable = (event) => {
      if (event.data.size > 0) {
        chunksRef.current.push(event.data);
      }
    };

    mediaRecorderRef.current.onstop = () => {
      const blob = new Blob(chunksRef.current, { type: "video/webm" });
      const url = URL.createObjectURL(blob);
      setRecordings((prev) => ({ ...prev, [currentQ]: url }));
      chunksRef.current = [];
      mediaStream.getTracks().forEach((track) => track.stop()); // stop cam
    };

    mediaRecorderRef.current.start();
    setRecording(true);
  };

  // Stop Video Recording
  const stopRecording = () => {
    mediaRecorderRef.current.stop();
    setRecording(false);
  };

  const nextQuestion = () => {
    if (currentQ < questions[mode].length - 1) {
      setCurrentQ(currentQ + 1);
    } else {
      setCompleted(true);
    }
  };

  return (
    <div style={{ backgroundColor: "#0f172a", minHeight: "100vh", color: "white", padding: "30px" }}>
      <h1 style={{ color: "#fbbf24" }}>üé• Mock Interview</h1>

      {/* Select Mode */}
      {!mode && (
        <div>
          <h2>Select Interview Mode:</h2>
          <button onClick={() => setMode("hr")} style={btnStyle}>HR Round</button>
          <button onClick={() => setMode("technical")} style={btnStyle}>Technical Round</button>
        </div>
      )}

      {/* Questions */}
      {mode && !completed && (
        <div>
          <h3>Question {currentQ + 1} of {questions[mode].length}</h3>
          <p style={{ fontSize: "18px", marginBottom: "15px" }}>
            {questions[mode][currentQ]}
          </p>

          {/* Live Video */}
          <video
            ref={videoRef}
            autoPlay
            muted
            style={{
              width: "400px",
              borderRadius: "10px",
              border: "2px solid #22d3ee",
              marginBottom: "15px",
            }}
          ></video>

          {/* Recording Controls */}
          {!recording ? (
            <button onClick={startRecording} style={{ ...btnStyle, background: "#22d3ee" }}>
              üéôÔ∏è Start Recording
            </button>
          ) : (
            <button onClick={stopRecording} style={{ ...btnStyle, background: "#f87171" }}>
              ‚èπÔ∏è Stop Recording
            </button>
          )}

          {/* Playback */}
          {recordings[currentQ] && (
            <div style={{ marginTop: "15px" }}>
              <p>‚ñ∂Ô∏è Your Answer:</p>
              <video controls src={recordings[currentQ]} style={{ width: "400px", borderRadius: "10px" }}></video>
            </div>
          )}

          <br />
          <button onClick={nextQuestion} style={btnStyle}>
            {currentQ < questions[mode].length - 1 ? "Next ‚û°Ô∏è" : "Finish ‚úÖ"}
          </button>
        </div>
      )}

      {/* Summary */}
      {completed && (
        <div>
          <h2>‚úÖ Interview Completed!</h2>
          <h3>Your Answers:</h3>
          <ul>
            {questions[mode].map((q, i) => (
              <li key={i} style={{ marginBottom: "25px" }}>
                <b>Q{i + 1}:</b> {q}
                <br />
                {recordings[i] ? (
                  <video controls src={recordings[i]} style={{ width: "400px", borderRadius: "10px" }}></video>
                ) : (
                  <i>No answer recorded</i>
                )}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

const btnStyle = {
  padding: "10px 20px",
  margin: "10px",
  borderRadius: "8px",
  background: "#34d399",
  color: "black",
  fontWeight: "bold",
  cursor: "pointer",
  border: "none",
};

export default MockInterview;
