import React, { useState } from "react";

const booksData = [
  {
    id: 1,
    title: "Reasoning Mastery",
    author: "R.S. Aggarwal",
    price: "‚Çπ450",
    img: "/images/reasoning.jpeg",
    link: "https://amzn.in/d/gRdIi14",
  },
  {
    id: 2,
    title: "Aptitude Skills",
    author: "Arun Sharma",
    price: "‚Çπ499",
    img: "/images/aptitude.jpeg",
    link: "https://amzn.in/d/9voi2fZ",
  },
  {
    id: 3,
    title: "Placement Tips",
    author: "Expert Panel",
    price: "‚Çπ299",
    img: "/images/placement_tips.jpeg",
    link: "https://amzn.in/d/4QRJJCO",
  },
  {
    id: 4,
    title: "HR Interview Guide",
    author: "Career Guru",
    price: "‚Çπ399",
    img: "/images/hr_interview.jpeg",
    link: "https://amzn.in/d/jksshdd",
  },
  {
    id: 5,
    title: "Coding Interview",
    author: "Narasimha Karumanchi",
    price: "‚Çπ699",
    img: "/images/coding_interview.jpeg",
    link: "https://amzn.in/d/a4NFh5r",
  },
];

function Books() {
  const [search, setSearch] = useState("");

  const filteredBooks = booksData.filter((book) =>
    book.title.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div
      style={{
        background: "linear-gradient(135deg, #0f172a, #1e293b, #0f172a)",
        minHeight: "100vh",
        color: "white",
        padding: "30px",
      }}
    >
      {/* Header */}
      <header
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          marginBottom: "30px",
        }}
      >
        <img
          src="/images/ppklogo.jpg"
          alt="Logo"
          style={{
            height: "90px",
            borderRadius: "50%",
            filter: "drop-shadow(0px 0px 20px gold)",
            marginRight: "20px",
            transition: "transform 0.4s ease",
          }}
          className="logo-img"
        />
        <div>
          <h1
            style={{
              fontSize: "34px",
              fontWeight: "bold",
              background: "linear-gradient(90deg, #facc15, #fbbf24, #f59e0b)",
              WebkitBackgroundClip: "text",
              WebkitTextFillColor: "transparent",
              textShadow: "0px 0px 15px rgba(255,215,0,0.9)",
              margin: 0,
            }}
          >
            Placement Preparation Kit
          </h1>
          <p style={{ color: "#22d3ee", margin: 0, fontWeight: "600" }}>
            üìö Explore our curated books for placements
          </p>
        </div>
      </header>

      {/* Search Bar */}
      <div style={{ textAlign: "center", marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="üîç Search books..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{
            padding: "12px 18px",
            borderRadius: "12px",
            border: "2px solid #22d3ee",
            fontSize: "16px",
            width: "60%",
            outline: "none",
          }}
        />
      </div>

      {/* Books Grid */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
          gap: "25px",
          padding: "0 20px",
        }}
      >
        {filteredBooks.length > 0 ? (
          filteredBooks.map((book) => (
            <div
              key={book.id}
              style={{
                background: "#1e293b",
                borderRadius: "14px",
                padding: "20px",
                textAlign: "center",
                transition: "transform 0.3s ease, box-shadow 0.3s ease",
                boxShadow: "0px 6px 12px rgba(0,0,0,0.6)",
              }}
              className="book-card"
            >
              <img
                src={book.img}
                alt={book.title}
                style={{
                  width: "100%",
                  height: "250px",
                  objectFit: "cover",
                  borderRadius: "12px",
                  marginBottom: "15px",
                }}
              />
              <h3 style={{ fontSize: "20px", color: "#fbbf24" }}>
                {book.title}
              </h3>
              <p style={{ margin: "5px 0", fontSize: "16px", color: "#e2e8f0" }}>
                {book.author}
              </p>
              <p
                style={{ fontWeight: "bold", fontSize: "18px", color: "#22d3ee" }}
              >
                {book.price}
              </p>
              <a
                href={book.link}
                target="_blank"
                rel="noopener noreferrer"
                style={{
                  display: "inline-block",
                  background: "#06b6d4",
                  padding: "10px 22px",
                  borderRadius: "10px",
                  fontSize: "16px",
                  fontWeight: "bold",
                  textDecoration: "none",
                  color: "white",
                  transition: "all 0.3s ease",
                }}
                className="buy-btn"
              >
                üõí Buy Now
              </a>
            </div>
          ))
        ) : (
          <p style={{ textAlign: "center", fontSize: "18px", color: "#f87171" }}>
            ‚ùå No books found.
          </p>
        )}
      </div>

      {/* Hover Effects */}
      <style>
        {`
          .book-card:hover {
            transform: scale(1.05);
            box-shadow: 0px 10px 20px rgba(255, 215, 0, 0.7);
          }
          .buy-btn:hover {
            background: #fbbf24;
            color: black;
          }
          .logo-img:hover {
            transform: rotate(10deg) scale(1.1);
            filter: drop-shadow(0px 0px 25px gold);
          }
        `}
      </style>
    </div>
  );
}

export default Books;
